     */
    public static function getInstance() {
        return Doctrine_Core::getTable('DeviceLog');
    }

    public static function addLog($device, $time, $descr) {

        $log = new DeviceLog();
        $log->device_id = is_object($device) ? $device->id : $device;
        $log->time = $time;
